import random

word_list = ["priti","college", "movie", "hostel", "lecture", "mathematics", "placement", "package", "internship", "assignment", "vishakha", "peace", "sumaiya", "mummy", "papa"]
chosen_word = random.choice(word_list)

#Testing code
print(f'The solution is {chosen_word}.')
display = []
for letter in chosen_word:
    display.append('_')
for letter in display:
    print(letter, end = ' ')
print()
end_of_game = False
lifeline = len(chosen_word) 
for i in range(len(chosen_word)):
    guess = input("\nGuess a letter: ").lower()
    count = 0
    for i, letter in enumerate(chosen_word):
        if guess == letter:
            display[i] = guess
            count = 1
    for letter in display:
        print(letter, end = ' ')
    if "_" not in display:
        end_of_game = True
        print("\n\nYou win, Game over!")
        break
    if count == 0:
        print("\nOops! wrong guess!\n")
    else:
        print("\nGood going!\n")
    lifeline = lifeline - 1
    if lifeline > 0:
        print(f"You can make {lifeline} more guesses!")
    else:
        print("\nYou have used all your lifelines!")
        if "_" in display:
            print("\n\nYou lose, Game over!")

if end_of_game == False:
    print(f"The correct word is {chosen_word}")



